static void render_slave(void) {
    if (get_current_wpm() > 0) {
        oled_on();
        OLED_TIMEOUT_TIME = timer_read32();
    }

    if (timer_elapsed32(OLED_TIMEOUT_TIME) > OLED_TIMOUT_LIMIT) {
        oled_off();
        return;
    }

    if (timer_elapsed32(ANIMATION_TIMER) > ANIM_FRAME_DURATION) {
        ANIMATION_TIMER = timer_read32();
        render_cat();
    }
}

static void render_master(void) {
    if (get_current_wpm() > 0) {
        oled_on();
        OLED_TIMEOUT_TIME = timer_read32();
    }

    if (timer_elapsed32(OLED_TIMEOUT_TIME) > OLED_TIMOUT_LIMIT) {
        oled_off();
        return;
    }

    char write_str[80];
    sprintf(write_str, "      %03d   WPM\n\n   %s", get_current_wpm(), read_layer_state());
    oled_clear();
    oled_write(write_str, false);
}

bool oled_task_user(void) {
    if (is_keyboard_master()) {
        render_master();
    } else {
        render_slave();
    }
    return false;
}
